import React, { useState, useEffect } from 'react';
import './App.css';
import SearchMovies from './SearchMovies';
import { getNewMovies, getPopularMovies, getMovieDetails, getSimilarMovies } from './api';

function App() {
  const [newMovies, setNewMovies] = useState([]);
  const [popularMovies, setPopularMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [similarMovies, setSimilarMovies] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [isDarkTheme, setIsDarkTheme] = useState(false); // Новый стейт для темы

  // Получение данных о новых и популярных фильмах
  useEffect(() => {
    const fetchMovies = async () => {
      const newMoviesResponse = await getNewMovies();
      const popularMoviesResponse = await getPopularMovies();
      setNewMovies(newMoviesResponse.data.results);
      setPopularMovies(popularMoviesResponse.data.results);
    };
    fetchMovies();
  }, []);

  // Обработка клика на карточку фильма
  const handleMovieClick = async (movieId) => {
    const response = await getMovieDetails(movieId);
    setSelectedMovie(response.data);

    const similarResponse = await getSimilarMovies(movieId);
    setSimilarMovies(similarResponse.data.results);

    setShowModal(true);
  };

  // Закрытие модального окна
  const handleCloseModal = () => setShowModal(false);

  // Функция для переключения темы
  const toggleTheme = () => {
    setIsDarkTheme((prevTheme) => !prevTheme);
  };

  return (
    <div className={`App ${isDarkTheme ? 'dark-theme' : ''}`}>
      <header className="App-header">
        <h1 className="App-title">
          <a href="/" className="text-decoration-none" style={{ color: isDarkTheme ? '#fff' : '#800080' }}>Movie Finder</a>
        </h1>
        <p className="App-subtitle" style={{ color: isDarkTheme ? '#aaa' : '#800080' }}>
          Discover the latest and most popular movies
        </p>
        {/* Переключатель темы */}
        <button onClick={toggleTheme} className="btn btn-toggle-theme">
          {isDarkTheme ? 'Light Theme' : 'Dark Theme'}
        </button>
      </header>

      <main>
        <SearchMovies />
        {/* Код отображения фильмов */}
        <div className="container mt-5">
          {/* New Releases и Popular Movies */}
        </div>
      </main>

      {/* Модальное окно для деталей фильма */}
      {showModal && selectedMovie && (
        <div className="modal fade show d-block" tabIndex="-1">
          {/* Содержимое модального окна */}
        </div>
      )}
    </div>
  );
}

export default App;
