import React, { useState } from 'react';
import { searchMovies } from './api'; // Импорт функции для поиска фильмов из api.js

const SearchMovies = () => {
  const [query, setQuery] = useState(''); // Хранит текст поиска
  const [movies, setMovies] = useState([]); // Хранит результаты поиска

  const handleSearch = async (e) => {
    e.preventDefault();
    if (query.trim() === '') return; // Проверка на пустой запрос
    try {
      const response = await searchMovies(query); // Запрос на поиск фильмов
      setMovies(response.data.results); // Сохраняем результаты в состояние
    } catch (error) {
      console.error('Ошибка поиска фильмов:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search for a movie"
        />
        <button type="submit">Search</button>
      </form>

      <div>
        {movies.length > 0 ? (
          movies.map((movie) => (
            <div key={movie.id}>
              <h3>{movie.title}</h3>
              <p>Rating: {movie.vote_average}</p>
            </div>
          ))
        ) : (
          <p>No movies found</p>
        )}
      </div>
    </div>
  );
};

export default SearchMovies;
