import React, { useState, useEffect } from 'react';
import { searchMovies, getGenres, getMovieDetails, getSimilarMovies, discoverMovies } from './api'; // Добавляем функцию для получения жанров
import { Modal, Button } from 'react-bootstrap';

const IMAGE_BASE_URL = 'https://image.tmdb.org/t/p/w500'; // Базовый URL для изображений

const SearchMovies = () => {
  const [query, setQuery] = useState('');
  const [movies, setMovies] = useState([]);
  const [genres, setGenres] = useState([]); // Состояние для списка жанров
  const [selectedGenre, setSelectedGenre] = useState(''); // Выбранный жанр
  const [year, setYear] = useState(''); // Выбранный год
  const [rating, setRating] = useState(''); // Выбранный рейтинг
  const [selectedMovie, setSelectedMovie] = useState(null); // Состояние для выбранного фильма
  const [similarMovies, setSimilarMovies] = useState([]); // Похожие фильмы
  const [showModal, setShowModal] = useState(false); // Для открытия/закрытия модального окна

  // Получаем список жанров при загрузке компонента
  useEffect(() => {
    const fetchGenres = async () => {
      const response = await getGenres();
      setGenres(response.data.genres);
    };
    fetchGenres();
  }, []);

  // Поиск фильмов по запросу
  const handleSearch = async (e) => {
    e.preventDefault();
    if (query.trim() !== '') {
      const response = await searchMovies(query);
      setMovies(response.data.results);
    } else if (year || selectedGenre || rating) {
      const response = await discoverMovies(year, selectedGenre, rating);
      setMovies(response.data.results);
    }
  };

  // Открыть модальное окно с информацией о фильме
  const handleMovieClick = async (movieId) => {
    const response = await getMovieDetails(movieId); // Получаем детали фильма
    setSelectedMovie(response.data); // Устанавливаем выбранный фильм в состояние

    const similarResponse = await getSimilarMovies(movieId); // Получаем похожие фильмы
    setSimilarMovies(similarResponse.data.results); // Устанавливаем похожие фильмы в состояние

    setShowModal(true); // Открыть модальное окно
  };

  const handleCloseModal = () => setShowModal(false); // Закрыть модальное окно

  return (
    <div>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search for a movie"
        />
        <button type="submit">Search</button>

        <div>
          <label>Year:</label>
          <input
            type="number"
            value={year}
            onChange={(e) => setYear(e.target.value)}
            placeholder="Enter year"
          />
        </div>

        <div>
          <label>Genre:</label>
          <select value={selectedGenre} onChange={(e) => setSelectedGenre(e.target.value)}>
            <option value="">Select genre</option>
            {genres.map((genre) => (
              <option key={genre.id} value={genre.id}>
                {genre.name}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Rating:</label>
          <input
            type="number"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            placeholder="Enter rating from 0 to 10"
            min="0"
            max="10"
          />
        </div>
      </form>

      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {movies.map((movie) => (
          <div
            key={movie.id}
            onClick={() => handleMovieClick(movie.id)} // Кликаем для открытия деталей
            style={{ cursor: 'pointer', maxWidth: '200px' }} // Курсор и стиль для кликабельности
          >
            {/* Обложка фильма */}
            {movie.poster_path ? (
              <img 
                src={`${IMAGE_BASE_URL}${movie.poster_path}`} 
                alt={movie.title} 
                style={{ width: '100%' }} 
                loading="lazy" // Добавляем ленивую загрузку
              />
            ) : (
              <div style={{ backgroundColor: '#ccc', width: '100%', height: '300px' }}>No Image</div>
            )}
            <h3>{movie.title}</h3>
            <p>Rating: {movie.vote_average}</p>
          </div>
        ))}
      </div>

      {/* Модальное окно для деталей фильма */}
      {selectedMovie && (
        <Modal show={showModal} onHide={handleCloseModal}>
          <Modal.Header closeButton>
            <Modal.Title>{selectedMovie.title}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>{selectedMovie.overview}</p>
            <h4>Similar Movies</h4>
            <ul>
              {similarMovies.map((similarMovie) => (
                <li key={similarMovie.id}>{similarMovie.title}</li>
              ))}
            </ul>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleCloseModal}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      )}
    </div>
  );
};

export default SearchMovies;
