import React, { useState, useEffect } from 'react';
import './App.css';
import SearchMovies from './SearchMovies';
import { getNewMovies, getPopularMovies } from './api'; // Импорт функций для новинок и популярных фильмов

function App() {
  const [theme, setTheme] = useState('light');
  const [newMovies, setNewMovies] = useState([]);
  const [popularMovies, setPopularMovies] = useState([]);

  useEffect(() => {
    document.body.setAttribute('data-theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  // Получаем новинки и популярные фильмы при загрузке страницы
  useEffect(() => {
    const fetchMovies = async () => {
      const newMoviesResponse = await getNewMovies();
      setNewMovies(newMoviesResponse.data.results);

      const popularMoviesResponse = await getPopularMovies();
      setPopularMovies(popularMoviesResponse.data.results);
    };

    fetchMovies();
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1 className="App-title">Film Finder</h1>
        <p className="App-subtitle">Discover the latest and most popular movies</p>
      </header>

      <main>
        <div className="d-flex justify-content-between align-items-center mb-3">
          <SearchMovies />
          {/* Кнопка переключения темы */}
          <button onClick={toggleTheme} className="btn-modern">
            Switch to {theme === 'light' ? 'Dark' : 'Light'} Theme
          </button>
        </div>

        {/* Блок с новинками */}
        <section>
          <h2 className="section-title">New Movies</h2>
          <div className="row justify-content-center">
            {newMovies.map((movie) => (
              <div className="col-lg-3 col-md-4 col-sm-6 mb-4" key={movie.id}>
                <div className="card h-100">
                  {movie.poster_path ? (
                    <img
                      src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                      alt={movie.title}
                      className="card-img-top"
                      loading="lazy"
                    />
                  ) : (
                    <div className="card-img-top" style={{ backgroundColor: '#ccc', height: '400px' }}>No Image</div>
                  )}
                  <div className="card-body">
                    <h5 className="card-title">{movie.title}</h5>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* Блок с популярными фильмами */}
        <section>
          <h2 className="section-title">Popular Movies</h2>
          <div className="row justify-content-center">
            {popularMovies.map((movie) => (
              <div className="col-lg-3 col-md-4 col-sm-6 mb-4" key={movie.id}>
                <div className="card h-100">
                  {movie.poster_path ? (
                    <img
                      src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                      alt={movie.title}
                      className="card-img-top"
                      loading="lazy"
                    />
                  ) : (
                    <div className="card-img-top" style={{ backgroundColor: '#ccc', height: '400px' }}>No Image</div>
                  )}
                  <div className="card-body">
                    <h5 className="card-title">{movie.title}</h5>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </section>
      </main>
    </div>
  );
}

export default App;
