import React, { useState, useEffect } from 'react';
import './App.css';
import SearchMovies from './SearchMovies';
import { getNewMovies, getPopularMovies } from './api';

function App() {
  const [theme, setTheme] = useState('light');
  const [newMovies, setNewMovies] = useState([]);
  const [popularMovies, setPopularMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);

  useEffect(() => {
    document.body.setAttribute('data-theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  useEffect(() => {
    const fetchMovies = async () => {
      const newMoviesResponse = await getNewMovies();
      const popularMoviesResponse = await getPopularMovies();
      setNewMovies(newMoviesResponse.data.results);
      setPopularMovies(popularMoviesResponse.data.results);
    };
    fetchMovies();
  }, []);

  const handleMovieClick = async (movie) => {
    setSelectedMovie(movie);
  };

  const handleCloseModal = () => setSelectedMovie(null);

  return (
    <div className="App">
      <header className="App-header">
        <h1 className="App-title">MOVIE FINDER</h1>
        <p className="App-subtitle">Discover the latest and most popular movies</p>
        <button onClick={toggleTheme} className="btn-modern">
          Switch to {theme === 'light' ? 'Dark' : 'Light'} Theme
        </button>
      </header>

      <main>
        <SearchMovies />
        <div className="container mt-5">
          {/* New Releases Section */}
          <h2 className="text-center">New Releases</h2>
          <div className="row justify-content-center">
            {newMovies.map((movie) => (
              <div className="col-lg-3 col-md-4 col-sm-6 mb-4" key={movie.id}>
                <div className="card h-100" onClick={() => handleMovieClick(movie)}>
                  {movie.poster_path ? (
                    <img 
                      src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} 
                      alt={movie.title} 
                      className="card-img-top"
                    />
                  ) : (
                    <div className="card-img-top" style={{ backgroundColor: '#ccc', height: '400px' }}>No Image</div>
                  )}
                  <div className="card-body">
                    <h5 className="card-title">{movie.title}</h5>
                    <p className="card-text">
                      {movie.vote_average} ★
                    </p>
                    <p className="card-year">{movie.release_date ? movie.release_date.substring(0, 4) : 'Unknown Year'}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Popular Movies Section */}
          <h2 className="text-center">Popular Movies</h2>
          <div className="row justify-content-center">
            {popularMovies.map((movie) => (
              <div className="col-lg-3 col-md-4 col-sm-6 mb-4" key={movie.id}>
                <div className="card h-100" onClick={() => handleMovieClick(movie)}>
                  {movie.poster_path ? (
                    <img 
                      src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} 
                      alt={movie.title} 
                      className="card-img-top"
                    />
                  ) : (
                    <div className="card-img-top" style={{ backgroundColor: '#ccc', height: '400px' }}>No Image</div>
                  )}
                  <div className="card-body">
                    <h5 className="card-title">{movie.title}</h5>
                    <p className="card-text">
                      {movie.vote_average} ★
                    </p>
                    <p className="card-year">{movie.release_date ? movie.release_date.substring(0, 4) : 'Unknown Year'}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </main>

      {/* Modal for Movie Details */}
      {selectedMovie && (
        <div className="modal" onClick={handleCloseModal}>
          <div className="modal-content">
            <h2>{selectedMovie.title}</h2>
            <p>{selectedMovie.overview}</p>
            <button onClick={handleCloseModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
